window.addEventListener("scroll",function(){let l=document.querySelector(".header");window.scrollY>100?l.classList.add("scrolled"):l.classList.remove("scrolled")});let links=document.querySelectorAll(".portfolio .links li"),boxes=document.querySelectorAll(".portfolio .box");links.forEach(l=>{l.onclick=()=>{links.forEach(l=>l.classList.remove("actives")),l.classList.add("actives");let s=l.classList.contains("all")?"all":l.classList.contains("active")?"active":"inactive";boxes.forEach(l=>{"all"===s?l.style.display="block":l.classList.contains(s)?l.style.display="block":l.style.display="none"})}});